mapper.select:
SELECT *
FROM raffle_activity_account_day


        user_id = #{userId}


        and activity_id = #{activityId}


        and day = #{day}

mapper.update:
update raffle_activity_account_day
set day_count_surplus = day_count_surplus - 1,
    update_time       = now()
where user_id = #{userId}
  and activity_id = #{activityId}
  and day = #{day}
  and day_count_surplus >= 1

update raffle_activity_account_day
set day_count_surplus = day_count_surplus - #{subtractor},
    update_time       = now()
where user_id = #{raffleActivityAccountDay.userId}
  and activity_id = #{raffleActivityAccountDay.activityId}
  and day = #{raffleActivityAccountDay.day}
  and day_count_surplus >= #{subtractor}

mapper.insert:
update raffle_activity_account_day
set day_count         = day_count + #{dayCount},
    day_count_surplus = day_count_surplus + #{dayCountSurplus},
    update_time       = now()
where user_id = #{userId}
  and activity_id = #{activityId}
  and day = #{day}
