mapper.select:
select *
from raffle_activity_account_month


        and user_id = #{userId}


        and activity_id = #{activityId}


        and month = #{month}

mapper.update:
update raffle_activity_account_month
set month_count_surplus = month_count_surplus - 1,
    update_time         = now()
where user_id = #{userId}
  and activity_id = #{activityId}
  and month = #{month}
  and month_count_surplus >= 1

update raffle_activity_account_month
set month_count_surplus = month_count_surplus - #{subtractor},
    update_time         = now()
where user_id = #{raffleActivityAccountMonth.userId}
  and activity_id = #{raffleActivityAccountMonth.activityId}
  and month = #{raffleActivityAccountMonth.month}
  and month_count_surplus >= #{subtractor}

mapper.insert:
update raffle_activity_account_month
set month_count         = month_count + #{monthCount},
    month_count_surplus = month_count_surplus + #{monthCountSurplus},
    update_time         = now()
where user_id = #{userId}
  and activity_id = #{activityId}
  and month = #{month}
